INSERT INTO `memorials` (`name`, `street`, `zip`, `city`, `description`) VALUES
	('B&uuml;cherverbrennung', 'Ehrenhof 1', 40479, 'D&uuml;sseldorf', 'Denkmal f&uuml;r die B&uuml;cherverbrennung der Nationalsozialisten 1933, dargestellt ist auf einer Gedenktafel das Zitat "Dort wo man B&uuml;cher verbrennt, verbrennt man auch am Ende Menschen" von Heinrich Heine.'),
	('Moritz Sommer Gedenktafel', 'Bogenstraße 35', 40227, 'D&uuml;sseldorf', 'Der 72-J&auml;hrige j&uuml;dische Mitb&uuml;rger Moritz Sommer wurde am 15.April 1945 auf dem Oberbilker Markt erh&auml;ngt. Er stand unter dem Verdacht, Deserteuren geholfen zu haben.'),
	('KZ-Gedenktafel', 'Am Stoffeler Kapellenweg 188', 40225, 'D&uuml;sseldorf', 'Von Oktober 1942 bis Februar 1943 befand sich hier ein Außenlager des KZ Sachsenhausen von den 500 H&auml;ftlingen starben mindestens 111.'),
	('Richtst&auml;tte Aktion Rheinl', 'Anton-Betz-Straße 3', 40223, 'D&uuml;sseldorf', 'Am 16.April 1945 fielen auf diesem Gel&auml;nde f&uuml;r die Befreiung der Stadt D&uuml;sseldorf von der nationalsozialistischen Gewaltherrschaft'),
	('Hoppeditz Denkmal', 'Marktplatz 5-6', 40213, 'D&uuml;sseldorf', 'Denkmal f&uuml;r Verk&ouml;rperung des heiter-kritischen "Rheinischen Geistes", dargestellt ist der Hoppeditz auf K&ouml;pfen, H&uuml;ten und Gl&ouml;ckchen.'),
	('Felix Mendelssohn-Bartholdy Sk', 'Heinrich-Heine-Allee', 40213, 'D&uuml;sseldorf', 'Das Felix Mendelssohn Bartholdy Denkmal wurde 1901 errichtet von D&uuml;sseldorfer B&uuml;rgern, um Felix Mendelssohn Bartholdy wegen seiner Musikverdienste zu ehren. Es wurde 1936 von Nazis vom Standort entfernt, da Felix Mendelssohn Bartholdy Jude war u'),
	('Ehra', 'Akademiestraße 1', 40213, 'D&uuml;sseldorf', 'Das Denkmal wurde am 52. Jahrestag der Befreiung von Auschwitz, dem 27. Januar 1997, am Alten Hafen aufgestellt. Seitdem dienen die Figur und ihre Umgebung als Ort f&uuml;r Gedenkveranstaltungen zur Erinnerung an die Get&ouml;teten des Nationalsozialismus'),
	('Glockenspiel', 'Bismarckstraße 90', 40210, 'D&uuml;sseldorf', 'Denkmal f&uuml;r Heimatvertriebene aus Osteuropa, dargestellt sind die verschiedenen Wappen, Glockenspiel t&auml;glich um 12 Uhr.');